mapper.select:
SELECT c.ct_code ,c.ct_name ,c.ct_status ,c.pk_name ,c.company_name ,c.relate_name ,c.contract_start_time ,c.contract_end_time ,e.last_billing_time ,e.current_billing_time ,e.receiving_date ,e.receivable_price ,e.has_tax ,e.tax_point ,e.pay_status ,COALESCE(e.tax_price, 0) AS "tax_price" ,(e.receivable_price + COALESCE(e.tax_price, 0)) AS "total_price" FROM t_yq_ati_con_electricity_receipt e LEFT JOIN t_yq_ati_con_contract c on e.ct_code = c.ct_code WHERE e.receiving_date BETWEEN #{startDate} and #{endDate} AND e.is_deleted = false AND c.is_deleted = false AND c.ct_status = #{ctStatus} AND c.cpy_code = #{cpyCode}
AND c.contractor_dept_code = #{contractorDeptCode}
AND c.pk_code = #{pkCode}
order by e.receiving_date

select
COALESCE(sum(w.paid_price), 0) as paidprice,
COALESCE(sum(w.receivable_price), 0) as receivableprice,
COALESCE(sum(w.tax_price), 0) as taxprice
FROM t_yq_ati_con_electricity_receipt w
LEFT JOIN t_yq_ati_con_contract c on w.ct_code = c.ct_code

    left join t_yq_pro_hos_park p on c.pk_code = p.pk_code

WHERE w.is_deleted = false
and c.is_deleted = false
and c.cpy_code = #{cpyCode}
and c.ct_status in ('4', '9')

    and w.receiving_date  >=  #{startDate}


    and  w.receiving_date  <=  #{endDate}


    and c.pk_code = #{pkCode}


    and p.territory_type = #{territoryType}


    and p.city_code = #{cityCode}


    and w.pay_status = #{payStatus}
